package com.firstdemo.pacakge;

public class AssignmentOpertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      //=,+=,-=,*=,/=,%=
		int num=59;
		float f=34;
		//num+=2;
		System.out.println(num);
		num+=f;
		System.out.println(num);
		f-=5;
		System.out.println(f);
		num*=f;
		System.out.println(num);
		f/=num;
		System.out.println(f);
		num%=f;
		System.out.println(num);
		
		
	}

}
